import React, { useState } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';
import Fade from '@material-ui/core/Fade';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import './User.css';

function User({ user }) {
    const [open, setOpen] = useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <div className="user">
            <List className="users__list">
                <ListItem button className="users__listitem" alignItems="flex-start" onClick={handleOpen}>
                    <ListItemAvatar>
                        <Avatar alt={user.real_name} src="/static/images/avatar/1.jpg" />
                    </ListItemAvatar>
                    <ListItemText
                        primary={user.real_name}
                        secondary={
                            <Typography
                                component="span"
                                variant="body2"
                                className="users__name"
                                color="textPrimary"
                            >
                                {user.tz}
                            </Typography>
                        }
                    />
                </ListItem>
                <Divider variant="inset" component="li" />
            </List>
            <Modal className="modal" open={open}
                onClose={handleClose}>
                <Fade in={open}>
                    <div className="modal__fade">
                        <form className="modal__dataepickerForm" noValidate>
                            <TextField
                                id="datetime-local"
                                type="datetime-local"
                                defaultValue="2017-05-24T10:30"
                                className="modal__datepicker"
                                InputLabelProps={{
                                    shrink: true,
                                }}
                            />
                        </form>
                        <TableContainer component={Paper}>
                            <Table className="table" aria-label="simple table">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Id</TableCell>
                                        <TableCell align="right">Start Time</TableCell>
                                        <TableCell align="right">End Time</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {user.activity_periods.map(() => (
                                        <TableRow key={row.name}>
                                            <TableCell component="th" scope="row">
                                                {row.name}
                                            </TableCell>
                                            <TableCell align="right">{row.calories}</TableCell>
                                            <TableCell align="right">{row.fat}</TableCell>
                                            <TableCell align="right">{row.carbs}</TableCell>
                                            <TableCell align="right">{row.protein}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </div>
                </Fade>
            </Modal>
        </div>
    )
}

export default User;
