import React, { useState } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import DateFnsUtils from '@date-io/date-fns';
import Grid from '@material-ui/core/Grid';
import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker,
} from '@material-ui/pickers';
import './Table.css';

function Tables({ userInfo }) {
    const [selectedEndDate, setSelectedEndDate] = useState(new Date());
    const [selectedStartDate, setSelectedStartDate] = useState(new Date());

    const formatDate = (date) => {
        const ye = new Intl.DateTimeFormat('en', { year: 'numeric' }).format(date);
        const mo = new Intl.DateTimeFormat('en', { month: 'short' }).format(date);
        const da = new Intl.DateTimeFormat('en', { day: '2-digit' }).format(date);
        const time = date.toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
        const format = mo + ' ' + da + ' ' + ye;
        return format;
    }

    const handleStartDateChange = (startDate) => {
        const formattedDate = formatDate(startDate);
        setSelectedStartDate(formattedDate);
    };

    const handleEndDateChange = (EndDate) => {
        const formattedDate = formatDate(EndDate);
        setSelectedEndDate(formattedDate);
    };
    console.log(selectedStartDate + "  " + selectedEndDate);
    return (
        <div>
            <div className="modal__fade">
                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                    <Grid container justify="space-around">
                        <KeyboardDatePicker
                            margin="normal"
                            format='MMM dd yyyy'
                            id="startDate-picker-dialog"
                            label="Start Date"
                            value={selectedStartDate}
                            onChange={handleStartDateChange}
                            KeyboardButtonProps={{
                                'aria-label': 'change date',
                            }}
                        />
                        <KeyboardDatePicker
                            margin="normal"
                            format='MMM dd yyyy'
                            id="endDate-picker-dialog"
                            label="End Date"
                            value={selectedEndDate}
                            onChange={handleEndDateChange}
                            KeyboardButtonProps={{
                                'aria-label': 'change date',
                            }}
                        />
                    </Grid>
                </MuiPickersUtilsProvider>
                <div className="table">
                    <TableContainer component={Paper}>
                        <Table className="table" aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell align="left" fontWeight="bold">Start Time</TableCell>
                                    <TableCell align="left" fontWeight="bold">End Time</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {userInfo.activity_periods.filter((time) => {
                                    const st = time.start_time.split(' ');
                                    st.pop();
                                    const sd = new Date(st);
                                    const et = time.end_time.split(' ');
                                    et.pop();
                                    const ed = new Date(et);
                                    console.log((selectedStartDate >= sd) && (selectedEndDate <= ed))
                                    return (sd >= selectedStartDate) || (ed <= selectedEndDate);
                                }).map((time, i) => (
                                    <TableRow key={time.start_time + i}>
                                        <TableCell component="th" scope="row">
                                            {time.start_time}
                                        </TableCell>
                                        <TableCell align="left">{time.end_time}</TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </div>
            </div>
        </div >

    )
}

export default Tables;
